// Transform extraction response to array format according to AGENT_INTERFACE_CONTRACTS
const filePath = $node["Document Validator"].json.file_path || "";
const isRetry = $node["Document Validator"].json.task === "retry_extraction";
const retryCount = $node["Document Validator"].json.retry_count || 0;
const documentId = $node["Document Validator"].json.document_id;

// Parse the LLM extraction result
try {
  const extractedData = $input.all()[0].body;

  // Handle response based on whether it's an array or single object
  let products = [];
  if (Array.isArray(extractedData)) {
    // Multiple products returned
    products = extractedData;
  } else {
    // Single product returned, wrap in array
    products = [extractedData];
  }

  // Transform products to match interface contract
  const transformedProducts = products.map(product => ({
    field_extractions: product
  }));

  // Extract language from the first product if available
  let language_detected = "unknown";
  if (products.length > 0 && products[0].language_detected && products[0].language_detected.value) {
    language_detected = products[0].language_detected.value;
  }

  // Prepare response according to AGENT_INTERFACE_CONTRACTS
  const result = {
    products: transformedProducts,
    language_detected: language_detected,
    _metadata: {
      prompt_id: isRetry ? "multimodal-v1.5-retry" : "multimodal-v1.5",
      model_version: "gemini-2.0-flash",
      generated_ts: new Date().toISOString(),
      is_retry: isRetry,
      retry_count: retryCount,
      multimodal_extraction: true,
      source_file_name: filePath.split('/').pop() || "unknown",
      product_count: transformedProducts.length
    },
    _lifecycle_log: $node["Document Validator"].json._lifecycle_log || [],
    document_id: documentId,
    retry_count: retryCount,
    original_request: isRetry ? $node["Document Validator"].json.original_request : $node["Document Validator"].json
  };

  // Add lifecycle log entry
  result._lifecycle_log.push({
    document_id: documentId,
    from_state: isRetry ? "RETRY_EXTRACTION" : "INTERPRETED",
    to_state: "EXTRACTED",
    timestamp: new Date().toISOString(),
    agent: "multimodal_metadata_extractor_v1",
    notes: isRetry ? 
      `Retry extraction attempt ${retryCount} completed - ${transformedProducts.length} product(s) extracted, language=${language_detected}` : 
      `Multimodal extraction completed - ${transformedProducts.length} product(s) extracted, language=${language_detected}`
  });

  return { json: result };
} catch (error) {
  // Error handling
  const errorEntry = {
    document_id: documentId,
    from_state: isRetry ? "RETRY_EXTRACTION" : "INTERPRETED",
    to_state: "FAILED",
    timestamp: new Date().toISOString(),
    agent: "multimodal_metadata_extractor_v1",
    notes: `Multimodal extraction failed: ${error.message}`
  };

  return {
    json: {
      task_status: "failed",
      error_summary: `Multimodal metadata extraction failed: ${error.message}`,
      document_id: documentId,
      retry_count: retryCount,
      _lifecycle_log: [...($node["Document Validator"].json._lifecycle_log || []), errorEntry]
    }
  };
}

