// Transform data processor response
try {
  const response = $input.all()[0].body;
  const documentId = $json.document_id;

  // Extract success and failed products from response
  const successProducts = response.products || [];
  const failedProducts = response.failed_products || [];
  const status = response.status || 'success';
  const reason = response.reason || '';

  // Get language from previous step
  const language_detected = $json.language_detected || "unknown";

  // Create lifecycle log entry
  const logEntry = {
    document_id: documentId,
    from_state: "EXTRACTED",
    to_state: status === 'failure' ? "FAILED" : "PROCESSED",
    timestamp: new Date().toISOString(),
    agent: "llm_data_processor_v1",
    notes: `${successProducts.length} products processed successfully, ${failedProducts.length} failed. ${reason}`
  };

  const result = {
    // Pass through successful products
    products: successProducts,
    failed_products: failedProducts,
    status: status,
    reason: reason,
    product_count: successProducts.length,
    partial_success: status === 'partial_success',
    document_id: documentId,
    language_detected: language_detected,
    original_request: $json.original_request,
    _lifecycle_log: [...($json._lifecycle_log || []), logEntry]
  };

  return { json: result };
} catch (error) {
  return {
    json: {
      task_status: "failed",
      error_summary: `Data processing failed: ${error.message}`,
      document_id: $json.document_id,
      _lifecycle_log: $json._lifecycle_log || []
    }
  };
}

