// Transform verifier response according to interface contracts
try {
  const response = $input.all()[0].body;
  const documentId = $json.document_id;

  // Extract verification results
  const verificationPassed = response.overall_verification?.passed || response.verification_passed || false;
  const verifiedProducts = response.products || [];
  const partialSuccess = response.overall_verification?.passed_count > 0 && 
                        response.overall_verification?.passed_count < response.overall_verification?.product_count;

  // Get language from previous steps
  const language_detected = $json.language_detected || "unknown";

  // Transform verified products to include extracted_values
  const transformedProducts = verifiedProducts.map(product => {
    // Extract values from field_extractions for final format
    const extractedValues = {};
    if (product.field_extractions) {
      for (const [field, data] of Object.entries(product.field_extractions)) {
        extractedValues[field] = data.value;
      }
    }

    return {
      ...product,
      extracted_values: extractedValues
    };
  });

  // Separate verified and failed products
  const successProducts = transformedProducts.filter(p => p.verification_passed);
  const failedProducts = transformedProducts.filter(p => !p.verification_passed);

  // Create lifecycle log entry
  const logEntry = {
    document_id: documentId,
    from_state: "PROCESSED",
    to_state: verificationPassed ? "VERIFIED" : "FAILED",
    timestamp: new Date().toISOString(),
    agent: "multimodal_verifier_v1",
    notes: `${successProducts.length} products verified, ${failedProducts.length} failed verification`
  };

  const result = {
    verification_passed: verificationPassed,
    reason: response.overall_verification?.notes || response.reason || '',
    products: successProducts,
    failed_products: failedProducts,
    verifier_version: "multimodal-v1.5",
    language_detected: language_detected,
    partial_success: partialSuccess,
    document_id: documentId,
    original_request: $json.original_request,
    _lifecycle_log: [...($json._lifecycle_log || []), logEntry]
  };

  return { json: result };
} catch (error) {
  return {
    json: {
      verification_passed: false,
      reason: `Verification failed: ${error.message}`,
      products: [],
      failed_products: $json.products || [],
      document_id: $json.document_id,
      _lifecycle_log: $json._lifecycle_log || []
    }
  };
}

