// Success Notifier Function for Materials Library Extraction
// Used by the Success Notifier node in n8n workflow

// Template loader function for email templates
const loadEmailTemplate = function(templateName, variables) {
  const fs = require('fs');
  const templatePath = `/home/node/email_templates/${templateName}.txt`;

  try {
    let template = fs.readFileSync(templatePath, 'utf8');

    // Replace placeholders with actual values
    for (const [key, value] of Object.entries(variables)) {
      const placeholder = new RegExp(`{{${key}}}`, 'g');
      template = template.replace(placeholder, value || '');
    }

    return template;
  } catch (error) {
    console.error(`Error loading email template ${templateName}:`, error);
    // Fallback to hardcoded template if file not found
    return `Subject: Email Template Error\n\nError loading email template: ${error.message}`;
  }
};

// Format Success Email Function
try {
  if (!items || items.length === 0) {
    throw new Error("No items received by Email Formatter");
  }

  const item = items[0];

  // Extract original request data
  const originalRequest = item.json.original_request || {};
  const sender = originalRequest.sender || "materials-team@example.com";

  // Get product count and language
  const productCount = item.json.products ? item.json.products.length : 0;
  const language = item.json.language_detected || "undetected";
  const partialSuccess = item.json.partial_success || false;

  // Format JSON for attachment - use extracted_values
  const cleanedProducts = item.json.products.map(product => product.extracted_values);
  const jsonStr = JSON.stringify(cleanedProducts, null, 2);

  // Build the product list for display
  let productList = '';
  if (productCount === 1) {
    const product = item.json.products[0].extracted_values;
    productList = `Material: ${product.name || 'Unnamed Material'}\n`;
    productList += `Brand: ${product.brand || 'Unspecified Brand'}\n`;
  } else {
    productList = 'Products:\n';
    const maxToList = Math.min(productCount, 5);
    for (let i = 0; i < maxToList; i++) {
      const product = item.json.products[i].extracted_values;
      productList += `${i+1}. ${product.name || 'Unnamed Material'} (${product.brand || 'Unspecified Brand'})\n`;
    }

    if (productCount > maxToList) {
      productList += `... and ${productCount - maxToList} more\n`;
    }
  }

  // Build the notes section
  let notes = '';
  const fallbackNote = item.json.products.some(p => p.fallback_applied) ? 
    'Note: Simplified metadata extracted for some products due to confidence thresholds.' : 
    '';

  if (fallbackNote) {
    notes += `\n${fallbackNote}\n`;
  }

  if (partialSuccess) {
    notes += `\nNote: Some products could not be fully verified and were excluded from the results.\n`;
  }

  // Build the product description
  let productDescription = '';
  if (productCount === 1) {
    productDescription = 'The extracted metadata is included below in JSON format.';
  } else {
    productDescription = `${productCount} products were extracted and their metadata is included below in JSON format.`;
  }

  // Load and populate the template
  const templateVars = {
    sender: sender,
    productCountSuffix: productCount > 1 ? ` (${productCount} products)` : '',
    productDescription: productDescription,
    productList: productList,
    notes: notes,
    language: language
  };

  const emailContent = loadEmailTemplate('success', templateVars);

  // Extract subject and body from template
  const lines = emailContent.split('\n');
  const subject = lines[0].replace('Subject: ', '');
  const body = lines.slice(2).join('\n');

  return [
    {
      json: {
        to: sender,
        subject: subject,
        body: body,
        attachments: [
          {
            data: Buffer.from(jsonStr).toString('base64'),
            name: 'extracted_metadata.json',
            type: 'application/json'
          }
        ],
        document_id: item.json.document_id,
        _lifecycle_log: item.json._lifecycle_log || []
      }
    }
  ];
} catch (error) {
  // Error handling
  return [
    {
      json: {
        to: "materials-team@example.com",
        subject: "Error Formatting Success Email",
        body: `There was an error formatting the success email: ${error.message}`,
        document_id: items[0]?.json?.document_id || "unknown",
        _lifecycle_log: items[0]?.json?._lifecycle_log || []
      }
    }
  ];
}

// Error Notifier Function for Materials Library Extraction
// Used by the Error Notifier node in n8n workflow

// Template loader function for email templates
const loadEmailTemplate = function(templateName, variables) {
  const fs = require('fs');
  const templatePath = `/home/node/email_templates/${templateName}.txt`;

  try {
    let template = fs.readFileSync(templatePath, 'utf8');

    // Replace placeholders with actual values
    for (const [key, value] of Object.entries(variables)) {
      const placeholder = new RegExp(`{{${key}}}`, 'g');
      template = template.replace(placeholder, value || '');
    }

    return template;
  } catch (error) {
    console.error(`Error loading email template ${templateName}:`, error);
    // Fallback to hardcoded template if file not found
    return `Subject: Email Template Error\n\nError loading email template: ${error.message}`;
  }
};

// Format Error Email Function
try {
  if (!items || items.length === 0) {
    throw new Error("No items received by Error Email Formatter");
  }

  const item = items[0];

  // Extract original request data
  const originalRequest = item.json.original_request || {};
  const sender = originalRequest.sender || "materials-team@example.com";

  // Get error reason - this could come from different paths
  const errorReason = item.json.error_summary || item.json.reason || "Unknown error occurred";

  // Check for failed products from different stages
  const failedProducts = item.json.failed_products || [];
  const failedCount = Array.isArray(failedProducts) ? failedProducts.length : 0;

  // Check for partial success scenarios
  const partialSuccess = item.json.partial_success || false;
  const products = item.json.products || [];
  const successCount = Array.isArray(products) ? products.length : 0;

  // Get language
  const language = item.json.language_detected || "undetected";

  // Build error details
  let errorDetails = '';

  // If this is a partial success, mention that some products succeeded
  if (partialSuccess && successCount > 0) {
    errorDetails += `Partial Success: ${successCount} product(s) were successfully processed.\n\n`;
  }

  // Add failed product details
  if (failedCount > 0) {
    errorDetails += `${failedCount} product(s) failed processing:\n`;

    // List reasons for first few failures
    const maxToList = Math.min(failedCount, 3);
    for (let i = 0; i < maxToList; i++) {
      const failedProduct = failedProducts[i];

      // Extract product name from various possible locations
      let productName = "Unknown product";
      if (failedProduct.extracted_values?.name) {
        productName = failedProduct.extracted_values.name;
      } else if (failedProduct.data?.field_extractions?.name?.value) {
        productName = failedProduct.data.field_extractions.name.value;
      } else if (failedProduct.data?.name?.value) {
        productName = failedProduct.data.name.value;
      }

      const productError = failedProduct.error || failedProduct.reason || "Validation failed";
      const productConfidence = failedProduct.confidence ? ` (confidence: ${failedProduct.confidence.toFixed(2)})` : '';

      errorDetails += `â€¢ ${productName}: ${productError}${productConfidence}\n`;
    }

    if (failedCount > maxToList) {
      errorDetails += `... and ${failedCount - maxToList} more\n`;
    }

    errorDetails += '\n';
  }

  // Add general error reason if no specific product failures
  if (failedCount === 0 && !partialSuccess) {
    errorDetails += `Error: ${errorReason}\n`;
  }

  // Load and populate the template
  const templateVars = {
    sender: sender,
    errorReason: errorReason,
    failedProductDetails: errorDetails,
    language: language
  };

  const emailContent = loadEmailTemplate('failure', templateVars);

  // Extract subject and body from template
  const lines = emailContent.split('\n');
  const subject = lines[0].replace('Subject: ', '');
  const body = lines.slice(2).join('\n');

  return [
    {
      json: {
        to: sender,
        subject: subject,
        body: body,
        document_id: item.json.document_id,
        _lifecycle_log: item.json._lifecycle_log || []
      }
    }
  ];
} catch (error) {
  // Error handling
  return [
    {
      json: {
        to: "materials-team@example.com",
        subject: "Error Formatting Error Email",
        body: `There was an error creating the error email: ${error.message}`,
        document_id: items[0]?.json?.document_id || "unknown",
        _lifecycle_log: items[0]?.json?._lifecycle_log || []
      }
    }
  ];
}

// Log all document lifecycle stages to a file

const fs = require('fs');
const path = require('path');

try {
  const logPath = '/home/node/data/logs';
  const logFile = path.join(logPath, 'document_lifecycle.json');

  // Ensure log directory exists
  if (!fs.existsSync(logPath)) {
    fs.mkdirSync(logPath, { recursive: true });
  }

  // Get existing logs or create new array
  let logs = [];
  if (fs.existsSync(logFile)) {
    try {
      const data = fs.readFileSync(logFile, 'utf8');
      logs = JSON.parse(data);
    } catch (err) {
      console.error('Error reading logs file:', err);
    }
  }

  // Add new log entries
  if (items[0].json._lifecycle_log && Array.isArray(items[0].json._lifecycle_log)) {
    logs = logs.concat(items[0].json._lifecycle_log);

    // Write updated logs
    try {
      fs.writeFileSync(logFile, JSON.stringify(logs, null, 2));
    } catch (err) {
      console.error('Error writing logs file:', err);
    }
  }

  // Pass through the original items
  return items;
} catch (error) {
  console.error('Error in Activity Logger:', error);
  // Pass through the original items even if logging fails
  return items;
}
