const fs = require('fs');

const inputData = $input.all().map(item => ({
  json: item.json, 
  binaryMeta: Object.keys(item.binary || {}).map(key => ({
    key: key,
    fileName: item.binary[key].fileName,
    mimeType: item.binary[key].mimeType,
    dataLength: item.binary[key].data ? item.binary[key].data.length : 0
  }))
}));
fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"Document Validator","phase":"input","data":inputData}) + '\n');

const items = [];

for (const item of $input.all()) {
  const email = item.json;
  const attachments = Object.keys(item.binary);

  const emailContext = {
    subject: email.subject,
    from: email.from,
    messageId: email.metadata && email.metadata["message-id"] ? email.metadata["message-id"] : email.messageId,
    date: email.date
  };

  items.push({
    json: {
      email: emailContext
    }
  });

  let pdfCount = 0;

  for (let i = 0; i < attachments.length; i++) {
    const attachmentKey = attachments[i];
    const attachment = item.binary[attachmentKey];

    if (attachment.mimeType === 'application/pdf') {
      const processItem = {
        json: {
          fileName: attachment.fileName || attachmentKey,
          fileSize: attachment.data.length,
          valid: true
        },
        binary: {
          pdf: attachment
        }
      };

      items.push(processItem);
      pdfCount++;
    }
  }

  if (pdfCount === 0) {
    items.push({
      json: {
        fileName: 'no-pdfs-found',
        valid: false,
        error: 'No PDF attachments found',
        errorType: 'validation'
      }
    });
  }
}

const outputData = items.map(item => ({
  json: item.json, 
  binaryMeta: Object.keys(item.binary || {}).map(key => ({
    key: key,
    fileName: item.binary[key].fileName,
    mimeType: item.binary[key].mimeType,
    dataLength: item.binary[key].data ? item.binary[key].data.length : 0
  }))
}));
fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"Document Validator","phase":"output","data":outputData}) + '\n');

return items;

const fs = require('fs');
const { GoogleGenerativeAI } = require('@google/generative-ai');

const inputData = {
  json: $input.item.json, 
  binaryMeta: Object.keys($input.item.binary || {}).map(key => ({
    key: key,
    fileName: $input.item.binary[key].fileName,
    mimeType: $input.item.binary[key].mimeType,
    dataLength: $input.item.binary[key].data ? $input.item.binary[key].data.length : 0
  }))
};
fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"input","data":inputData}) + '\n');

if ($input.item.json.email && !$input.item.json.fileName) {
  const outputData = {
    json: $input.item.json, 
    binaryMeta: []
  };
  fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"output","data":outputData}) + '\n');
  return $input.item;
}

if (!$input.item.json.valid) {
  const outputData = {
    json: $input.item.json, 
    binaryMeta: Object.keys($input.item.binary || {}).map(key => ({
      key: key,
      fileName: $input.item.binary[key].fileName,
      mimeType: $input.item.binary[key].mimeType,
      dataLength: $input.item.binary[key].data ? $input.item.binary[key].data.length : 0
    }))
  };
  fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"output","data":outputData}) + '\n');
  return $input.item;
}

const item = $input.item;
const pdfData = item.binary.pdf.data;

if (!pdfData) {
  const result = {
    json: {
      ...item.json,
      valid: false,
      error: 'No PDF data found',
      errorType: 'extraction'
    }
  };
  const outputData = {
    json: result.json, 
    binaryMeta: Object.keys(result.binary || {}).map(key => ({
      key: key,
      fileName: result.binary[key].fileName,
      mimeType: result.binary[key].mimeType,
      dataLength: result.binary[key].data ? result.binary[key].data.length : 0
    }))
  };
  fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"output","data":outputData}) + '\n');
  return result;
}

try {
  const promptPath = '/home/node/prompts/llm_extraction.txt';
  const prompt = fs.readFileSync(promptPath, 'utf8');
  const schemaPath = '/home/node/schema/materials_schema.json';
  const schema = JSON.parse(fs.readFileSync(schemaPath, 'utf8'));

  const apiKey = $env.LLM_API_KEY;
  console.log('Using API Key:', apiKey ? `${apiKey.substring(0, 10)}...` : 'NOT SET');
  console.log('Processing file:', item.json.fileName);
  console.log('PDF data length:', pdfData.length);

  if (!apiKey) {
    throw new Error('LLM_API_KEY is not defined in environment variables.');
  }

  // Initialize the SDK
  const genAI = new GoogleGenerativeAI(apiKey);

  // Get the model - extract model name from endpoint if available
  const modelMatch = $env.LLM_API_ENDPOINT?.match(/\/models\/([^:/]+)/);
  const modelName = modelMatch ? modelMatch[1] : 'gemini-1.5-flash';

  console.log('Using model:', modelName);

  const model = genAI.getGenerativeModel({ 
    model: modelName,
    generationConfig: {
      temperature: 0.1,
      topK: 32,
      topP: 0.95,
      maxOutputTokens: 8192,
      responseMimeType: "application/json",
      responseSchema: schema
    }
  });

  console.log('Making API request to Gemini for:', item.json.fileName);

  // Create the request
  const result = await model.generateContent([
    prompt + "\n\nAnalyze this PDF and extract all product metadata with confidence scores. Return the result as valid JSON matching the required schema.",
    {
      inlineData: {
        mimeType: "application/pdf",
        data: pdfData
      }
    }
  ]);

  const response = await result.response;
  const extractedContent = response.text();

  console.log('API Response received for:', item.json.fileName);

  if (!extractedContent) {
    throw new Error('No content in API response');
  }

  const extractedData = JSON.parse(extractedContent);

  if (extractedData.products && Array.isArray(extractedData.products)) {
    extractedData.products.forEach(product => {
      if (!product.source_file_name) {
        product.source_file_name = {
          value: item.json.fileName,
          confidence: 1.0
        };
      }
    });
  }

  const result = {
    json: {
      ...item.json,
      valid: true,
      extractedData: extractedData,
      productCount: extractedData.products.length
    },
    binary: item.binary
  };

  const outputData = {
    json: result.json, 
    binaryMeta: Object.keys(result.binary || {}).map(key => ({
      key: key,
      fileName: result.binary[key].fileName,
      mimeType: result.binary[key].mimeType,
      dataLength: result.binary[key].data ? result.binary[key].data.length : 0
    }))
  };
  fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"output","data":outputData}) + '\n');

  return result;

} catch (error) {
  console.error('Extraction failed for:', item.json.fileName, error.message);

  // Log more detailed error information
  if (error.response) {
    console.error('API Error Response:', error.response);
  }

  const result = {
    json: {
      ...item.json,
      valid: false,
      error: error.message,
      errorType: 'extraction'
    }
  };

  const outputData = {
    json: result.json, 
    binaryMeta: Object.keys(result.binary || {}).map(key => ({
      key: key,
      fileName: result.binary[key].fileName,
      mimeType: result.binary[key].mimeType,
      dataLength: result.binary[key].data ? result.binary[key].data.length : 0
    }))
  };
  fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"LLM Extraction","phase":"output","data":outputData}) + '\n');

  return result;
}

const fs = require('fs');

function getModelFromEndpoint(endpoint) {
  if (!endpoint) return '';
  const match = endpoint.match(/\/models\/([^:/]+)/);
  return match ? match[1] : '';
}

function escapeHtml(text) {
  if (!text) return '';
  return String(text)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

const usedModel = getModelFromEndpoint($env.LLM_API_ENDPOINT);

let modelInfo = '';
if (usedModel) {
  modelInfo = ` using model <strong>${escapeHtml(usedModel)}</strong>`;
}

const inputData = $input.all().map(item => ({
  json: item.json, 
  binaryMeta: Object.keys(item.binary || {}).map(key => ({
    key: key,
    fileName: item.binary[key].fileName,
    mimeType: item.binary[key].mimeType,
    dataLength: item.binary[key].data ? item.binary[key].data.length : 0
  }))
}));
fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"Result Processor","phase":"input","data":inputData}) + '\n');

const allItems = $input.all();
const emailContextItem = allItems.find(item => item.json.email && !item.json.fileName);
if (!emailContextItem) {
  throw new Error('Email context item not found in input');
}
const emailContext = emailContextItem.json.email;

const pdfItems = allItems.filter(item => item.json.fileName || item.json.error);
const validPdfs = pdfItems.filter(i => i.json.fileName !== 'no-pdfs-found');
const totalAttachments = validPdfs.length;

function formatValue(field) {
  if (Array.isArray(field.value)) {
    return field.value.join(', ');
  }
  return String(field.value);
}

function createProductTable(product, fileName) {
  const supplier = formatValue(product.supplier);
  const productName = formatValue(product.product_name);
  const sku = formatValue(product.sku_number);

  let html = `
    <table class="header-table">
      <tr>
        <th>Supplier</th>
        <th>Product Name</th>
        <th>SKU</th>
        <th>Source File</th>
      </tr>
      <tr>
        <td>${escapeHtml(supplier)}</td>
        <td>${escapeHtml(productName)}</td>
        <td>${escapeHtml(sku)}</td>
        <td>${escapeHtml(fileName)}</td>
      </tr>
    </table>
  `;

  html += `
    <table class="data-table">
      <tr>
        <th>Field</th>
        <th>Value</th>
      </tr>
  `;

  for (const [fieldKey, fieldData] of Object.entries(product)) {
    if (['supplier', 'product_name', 'sku_number', 'source_file_name'].includes(fieldKey)) continue;

    if (fieldData && typeof fieldData === 'object' && fieldData.value !== null && fieldData.value !== undefined && fieldData.value !== '') {
      const value = formatValue(fieldData);
      if (value && value.trim && value.trim() !== '') {
        const fieldLabel = fieldKey.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
        html += `
      <tr>
        <td>${fieldLabel}</td>
        <td>${escapeHtml(value)}</td>
      </tr>`;
      }
    }
  }

  html += `
    </table>
  `;

  return html;
}

function createRequestDetailsTable(emailContext) {
  return `
    <table>
      <tr>
        <th>From</th>
        <td>${escapeHtml(emailContext.from)}</td>
      </tr>
      <tr>
        <th>Subject</th>
        <td>${escapeHtml(emailContext.subject)}</td>
      </tr>
      <tr>
        <th>Date</th>
        <td>${escapeHtml(emailContext.date)}</td>
      </tr>
      <tr>
        <th>Message ID</th>
        <td>${escapeHtml(emailContext.messageId)}</td>
      </tr>
    </table>
  `;
}

function createFailedFilesSection(errors) {
  if (errors.length === 0) {
    return '';
  }

  let html = `
    <div class="failed-files">
        <h3>Processing Issues</h3>
        <p>The following files could not be processed:</p>
        <ul>`;

  errors.forEach(error => {
    html += `<li><strong>${escapeHtml(error.fileName)}:</strong> ${escapeHtml(error.error)}</li>`;
  });

  html += `
        </ul>
    </div>`;

  return html;
}

const allProducts = [];
const errors = [];
const summaries = [];
const exceptions = [];

for (const item of pdfItems) {
  if (!item.json.valid) {
    errors.push({
      fileName: item.json.fileName,
      error: item.json.error,
      errorType: item.json.errorType
    });
    continue;
  }

  const products = item.json.extractedData.products;
  const summary = item.json.extractedData.processing_summary;
  const processingExceptions = item.json.extractedData.processing_exceptions;

  if (summary) {
    summaries.push(`${item.json.fileName}: ${summary}`);
  }

  if (processingExceptions) {
    if (Array.isArray(processingExceptions)) {
      exceptions.push(...processingExceptions.map(ex => `${item.json.fileName}: ${ex}`));
    } else if (typeof processingExceptions === 'string') {
      exceptions.push(`${item.json.fileName}: ${processingExceptions}`);
    }
  }

  for (const product of products) {
    allProducts.push({
      product: product,
      fileName: item.json.fileName
    });
  }
}

const status = allProducts.length > 0 ? 'success' : 'failure';
const requestDetails = createRequestDetailsTable(emailContext);
let emailBody = '';

if (status === 'success') {
  const templatePath = '/home/node/email_templates/success.html';
  let template = fs.readFileSync(templatePath, 'utf8');

  let productTables = '';
  allProducts.forEach((productInfo, idx) => {
    if (idx > 0) productTables += '<br>';
    productTables += createProductTable(productInfo.product, productInfo.fileName);
  });

  if (summaries.length > 0) {
    extractionSummary = '<ul>' + summaries.map(summary => `<li>${escapeHtml(summary)}</li>`).join('') + '</ul>';
  } else {
    extractionSummary = `<ul><li>Processed ${allProducts.length} product(s) from ${totalAttachments} attachment(s).</li></ul>`;
  }
  extractionSummary += `<div>${modelInfo}</div>`;

  let exceptionsSection = '';
  if (exceptions.length > 0) {
    exceptionsSection = `
    <div class="exceptions">
        <h3>Processing Exceptions</h3>
        <ul>`;
    exceptions.forEach(exception => {
      exceptionsSection += `<li>${escapeHtml(exception)}</li>`;
    });
    exceptionsSection += `
        </ul>
    </div>`;
  }

  const failedFilesSection = createFailedFilesSection(errors);

  emailBody = template
    .replace('{{productTables}}', productTables)
    .replace('{{extractionSummary}}', extractionSummary)
    .replace('{{exceptionsSection}}', exceptionsSection)
    .replace('{{failedFilesSection}}', failedFilesSection)
    .replace('{{requestDetails}}', requestDetails)
    .replace('{{totalProducts}}', allProducts.length)
    .replace('{{totalAttachments}}', totalAttachments);

} else {
  const templatePath = '/home/node/email_templates/failure.html';
  let template = fs.readFileSync(templatePath, 'utf8');

  let errorDetails = '';
  if (errors.length > 0) {
    errorDetails = errors.map(e => `<strong>${e.fileName}:</strong> ${e.error}`).join('<br>');
  } else {
    errorDetails = 'No specific error details available.';
  }

  emailBody = template
    .replace('{{errorDetails}}', errorDetails)
    .replace('{{requestDetails}}', requestDetails)
    .replace('{{totalAttachments}}', totalAttachments);
}

const result = {
  json: {
    to: emailContext.from,
    subject: `Re: ${emailContext.subject} - Materials Extraction ${status === 'success' ? 'Complete' : 'Failed'}`,
    body: emailBody,
    messageId: emailContext.messageId,
    status: status,
    processingTimestamp: new Date().toISOString()
  }
};

fs.appendFileSync('/home/node/data/debug.log', JSON.stringify({"timestamp":new Date().toISOString(),"executionId":$execution.id,"node":"Result Processor","phase":"output","data":[result]}) + '\n');

return [result];
